import React from 'react';
import {useParams} from 'react-router-dom';
import Input from '../../shared/components/FormElements/Button';
import { VALIDATOR_MINLENGTH, VALIDATOR_REQUIRE } from '../../shared/util/validators';
import {VALIDATOR} from '../../shared/util/'

const DUMMY_PLACES=[
    {
        id: 'p1',
        title:'Empire State Buildings',
        description:'One of the most famous scrapers',
        imageUrl: 'https://i.pinimg.com/280x280_RS/dc/f5/33/dcf533f965c3ad3315ba7f2a1c8542c4.jpg',
        address:'20 W 34th St,New York',
        location:{
            lat:40.7484405,
            lng:-73.9878584
        },
        creator:'u1'
    },

     {
        id: 'p1',
        title:'Brm Brm',
        description:'One of the most famous scrapers',
        imageUrl: 'https://i.pinimg.com/280x280_RS/dc/f5/33/dcf533f965c3ad3315ba7f2a1c8542c4.jpg',
        address:'20 W 34th St,New York',
        location:{
            lat:40.7484405,
            lng:-73.9878584
        },
        creator:'u2'
    }
];

const UpdatePlace = () => {
    const placeId=useParams().placeId;
    const identifiedPlace=DUMMY_PLACES.find(p=>p.id===placeId);
    if(!identifiedPlace){
        return(
            <div className="center">
                <h2></h2>
            </div>
        )
    }

    return <form>
        <Input 
        id="title" 
        element="input"
         type="text" 
         label="Title" 
         validators={[VALIDATOR_REQUIRE()]}
         errorText="Please enter a valid title"
         onInput={()=>{}}
        value={identifiedPlace.title}
        valid={true}
        />

        <Input 
        id="description" 
        element="textarea"
        
         label="Description" 
         validators={[VALIDATOR_MINLENGTH(5)]}
         errorText="Please enter a valid description (min 5 characters)"
         onInput={()=>{}}
        value={identifiedPlace.description}
        valid={true}
        />

        <Button type="submit" disabled={true}>
        UPDATE PLACE
        </Button>

         </form>

};

export default UpdatePlace;
