import React, { useContext } from 'react';
import { NavLink } from 'react-router-dom';
import { AuthContext } from '../../context/auth-context';
import './NavLinks.css';
import Gallery from '../../../graffiti/components/Gallery';
// import SearchBox from '../FormElements/SearchBox';
// import MyProfile from '../../../user/pages/MyProfile';
const NavLinks = props => {
  const auth = useContext(AuthContext);
  return (
    <ul className="nav-links">

      {/* {auth.isLoggedIn && (
        <li>
          <SearchBox/>
        </li>
      )} */}

      <li>
        <NavLink to="/" exact>
          ALL USERS
        </NavLink>
      </li>

     

      {auth.isLoggedIn && (
        <li>
          <NavLink to={`/${auth.userId}/places`}>MY GRAFFITI</NavLink>
        </li>
      )}
      {auth.isLoggedIn && (
        <li>
          <NavLink to="/places/new">ADD GRAFFITi</NavLink>
        </li>
      )}

       {auth.isLoggedIn && (
        <li>
          <Gallery images={IMAGES} backdropClosesModal={true}/>
        </li>
      )}

      {/* {auth.isLoggedIn && (
        <li>
          <NavLink to={'/myprofile'}>MY PROFILE</NavLink>
        </li>
      )} */}

      {!auth.isLoggedIn && (
        <li>
          <NavLink to="/auth">AUTHENTICATE</NavLink>
        </li>
      )}
      {auth.isLoggedIn && (
        <li>
          <button onClick={auth.logout}>LOGOUT</button>
        </li>
      )}
    </ul>
  );
};

export default NavLinks;
