import React from 'react';

class Vote extends React.Component{
  
    state = {
      votecount: 32,
      upvoted: false,
      downvoted: false
    }
  
    handleClickDownvote = () => {
      if (this.state.upvoted) {
        this.setState({
          votecount: this.state.votecount - 2,
          downvoted: !this.state.downvoted,
          upvoted: false
        })
      } else if (this.state.downvoted) {
        this.setState({
          votecount: this.state.votecount + 1,
          upvoted: false,
          downvoted: false
        })
      } else {
        this.setState({
          downvoted: !this.state.downvoted,
          upvoted: false
        })
        this.state.downvoted ? this.increase() : this.decrease()
      }
    }
  
    handleClickUpvote = () => {
      if (this.state.downvoted) {
        this.setState({
          votecount: this.state.votecount + 2,
          upvoted: !this.state.upvoted,
          downvoted: false
        })
      } else if (this.state.upvoted) {
        this.setState({
          votecount: this.state.votecount - 1,
          upvoted: false,
          downvoted: false
        })
      } else {
        this.setState({
          upvoted: !this.state.upvoted,
          downvoted: false
        })
        this.state.upvoted ? this.decrease() : this.increase()
      }
    }
    
    increase() {
      this.setState({
        votecount: this.state.votecount + 1
      })
    }
  
    decrease() {
      this.setState({
        votecount: this.state.votecount - 1
      })
    }
  
    render() {
      return (
        <div className="voiting">
          <button onClick={this.handleClickDownvote} className="downvote">
            Downvote
          </button>
          <div className="votecount">
            {this.state.votecount}
          </div>
          <button onClick={this.handleClickUpvote} className="upvote">
            Upvote
          </button>
        </div>
      )
    }
  }
  export default GraffitiList;
  ReactDOM.render(<Vote />, document.getElementById("root"));
