import React,{Suspense} from 'react';
import {
  BrowserRouter as Router,
  Route,
  Redirect,
  Switch
} from 'react-router-dom';

//import Users from './user/pages/Users';
//import NewPlace from './places/pages/NewPlace';
//import UserPlaces from './places/pages/UserPlaces';
//import UpdatePlace from './places/pages/UpdatePlace';
//import Auth from './user/pages/Auth';
import MainNavigation from './shared/components/Navigation/MainNavigation';
import LoadingSpinner from './shared/components/UIElements/LoadingSpinner';
import { AuthContext } from './shared/context/auth-context';
import { useAuth } from './shared/hooks/auth-hook';

// const Users=React.lazy(()=>import('./user/pages/Users'));
// const NewPlace=React.lazy(()=>import('./places/pages/NewPlace'));
// const UserPlaces=React.lazy(()=>import('./places/pages/UserPlaces'));
// const UpdatePlace=React.lazy(()=>import('./places/pages/UpdatePlace'));

const Users=React.lazy(()=>import('./user/pages/Users'));
const NewPlace=React.lazy(()=>import('./graffiti/pages/NewPlace'));
const UserPlaces=React.lazy(()=>import('./graffiti/pages/UserPlaces'));
const UpdatePlace=React.lazy(()=>import('./graffiti/pages/UpdatePlace'));

const Auth=React.lazy(()=>import('./user/pages/Auth'));

const App = () => {
  const { token, login, logout, userId } = useAuth();

  let routes;

  if (token) {
    routes = (
      <Switch>
        <Route path="/" exact>
          <Users />
        </Route>
        <Route path="/:userId/graffiti" exact>
          <UserPlaces />
        </Route>
        <Route path="/graffiti/new" exact>
          <NewPlace />
        </Route>
        <Route path="/graffiti/:placeId">
          <UpdatePlace />
        </Route>
        <Redirect to="/" />
      </Switch>
    );
  } else {
    routes = (
      <Switch>
        <Route path="/" exact>
          <Users />
        </Route>
        <Route path="/:userId/graffiti" exact>
          <UserPlaces />
        </Route>
        <Route path="/auth">
          <Auth />
        </Route>
        <Redirect to="/auth" />
      </Switch>
    );
  }

  return (
    <AuthContext.Provider
      value={{
        isLoggedIn: !!token,
        token: token,
        userId: userId,
        login: login,
        logout: logout
      }}
    >
      <Router>
        <MainNavigation />
        <main>
          <Suspense 
          fallback={
          <div className="center">
           <LoadingSpinner/>
            </div>
          }
          >
              {routes}</Suspense></main>
      </Router>
    </AuthContext.Provider>
  );
};

export default App;
