import React from 'react';
import './Vote.css';
const seed = [
  { id: 5544, value: 0 },
 
]

class ProductItem extends React.Component {
  handleUpvote = (e) => {
    const type = e.target.innerText // is upvote or downvote
    const { upDownVote, id } = this.props;
    upDownVote(id, type);
  }
  render() {
    const {  value } = this.props;
    return (
      <div>
      
        {'  '}<span className="button_vote" onClick={this.handleUpvote}>upvote</span>
        {'  '}<span className="button_vote" onClick={this.handleUpvote}>downvote</span>
        {value}
      

<ul class="list-unstyled">
      <li class="entry">
          <div class="row">
               <div class="col-sm-2 entry__rank">
                     <span class="pull-left">1.</span>
                      <i class="fa fa-icon fa-caret-up fa-2x pull-left"></i>
                      <i class="fa fa-icon fa-caret-down fa-2x pull-left"></i>
                 </div>
                  <div class="col-md-9">
                      
                  </div>
             </div>
         </li>
    </ul>

    </div>



    )
  }
}

const ProductList = ({ products, upDownVote }) => {
  return (
    products.map(({ id, value }, key) => {
      return (
        <ProductItem
          key={`product-${id}`}
          id={id}
          value={value}
          upDownVote={upDownVote}
        />
      )
    })
  )
}




class Vote extends React.Component {
  state = {
    products: [],
  }

  componentDidMount() {
    this.setState({ products: seed })
  }

  handleVote = (id, type) => {
    const updateVote = this.state.products.map((prod, key) => {
      if (prod.id === id) {
        type === 'upvote' ? prod.value++ : prod.value--
      }
      return prod;
    });

    this.setState({products: updateVote});
  }
  render() {
    return (
      <div>
        <ProductList
          products={this.state.products}
          upDownVote={this.handleVote}
        />
      </div>
    );
  }
}


export default Vote;
